parameters.jsCode:
const payload = $json.body ?? $json;
const esc = (value) => String(value ?? '').trim().replace(/'/g, "''");
const action = String(payload.action ?? '').trim();
const allowed = ['create_board', 'join_board', 'add_item', 'get_board'];

if (!allowed.includes(action)) {
  return [{ json: { action: 'invalid', error: 'Unsupported action' } }];
}

const incomingCode = String(payload.board_code ?? '').trim().toUpperCase();
const generatedCode = `BD${Math.random().toString(36).slice(2, 8).toUpperCase()}`;
const boardCode = incomingCode || generatedCode;

return [{
  json: {
    action,
    username: String(payload.username ?? '').trim(),
    pin: String(payload.pin ?? '').trim(),
    board_name: String(payload.board_name ?? 'Shared List').trim(),
    board_code: boardCode,
    item_name: String(payload.item_name ?? '').trim(),
    target_price: Number(payload.target_price ?? 0),
    start_date: String(payload.start_date ?? ''),
    end_date: String(payload.end_date ?? ''),
    username_sql: esc(payload.username),
    pin_sql: esc(payload.pin),
    board_name_sql: esc(payload.board_name ?? 'Shared List'),
    board_code_sql: esc(boardCode),
    item_name_sql: esc(payload.item_name)
  }
}];

