const payload = $json.body ?? $json;
const esc = (value) => String(value ?? "").trim().replace(/'/g, "''");
const action = String(payload.action ?? "").trim().toLowerCase();
const allowed = ["login", "create_board", "join_board", "add_item", "analyze_item", "get_board"];

if (!allowed.includes(action)) {
  return [{ json: { action: "invalid", error: "Unsupported action" } }];
}

const incomingCode = String(payload.board_code ?? "").trim().toUpperCase();
const generatedCode = "BD" + Math.random().toString(36).slice(2, 8).toUpperCase();
const boardCode = incomingCode || generatedCode;

return [{
  json: {
    action,
    username: String(payload.username ?? "").trim(),
    pin: String(payload.pin ?? "").trim(),
    board_name: String(payload.board_name ?? "Shared List").trim(),
    board_code: boardCode,
    item_id: Number(payload.item_id ?? 0),
    item_name: String(payload.item_name ?? "").trim(),
    target_price: Number(payload.target_price ?? 0),
    start_date: String(payload.start_date ?? ""),
    end_date: String(payload.end_date ?? ""),
    username_sql: esc(payload.username),
    pin_sql: esc(payload.pin),
    board_name_sql: esc(payload.board_name ?? "Shared List"),
    board_code_sql: esc(boardCode),
    item_name_sql: esc(payload.item_name),
  }
}];

const input = $("Normalize Input").first().json;
const target = Number(input.target_price ?? 0);

let marketPrice = Number.isFinite(target) ? Number((target * 1.05).toFixed(2)) : 0;
let alternatives = [
  { name: ("Used " + String(input.item_name ?? "")).trim(), estimated_price: Number((marketPrice * 0.7).toFixed(2)) },
  { name: ("Budget alternative to " + String(input.item_name ?? "")).trim(), estimated_price: Number((marketPrice * 0.8).toFixed(2)) },
  { name: (String(input.item_name ?? "") + " previous model").trim(), estimated_price: Number((marketPrice * 0.85).toFixed(2)) },
];
let summary = "";
let recommendation = "";

const rawText = $json?.candidates?.[0]?.content?.parts?.[0]?.text ?? $json?.output ?? $json?.response ?? $json?.text ?? (typeof $json === "string" ? $json : "");
if (typeof rawText === "string" && rawText.trim()) {
  const cleaned = rawText.replace(/```json|```/g, "").trim();
  const maybeJson = cleaned.match(/{[sS]*}/)?.[0] ?? cleaned;

  try {
    const aiData = JSON.parse(maybeJson);

    if (Number.isFinite(Number(aiData?.market_price))) {
      marketPrice = Number(aiData.market_price);
    }

    if (Array.isArray(aiData?.alternatives) && aiData.alternatives.length) {
      alternatives = aiData.alternatives.slice(0, 3).map((alt) => ({
        name: String(alt?.name ?? "Alternative option").trim(),
        estimated_price: Number.isFinite(Number(alt?.estimated_price)) ? Number(alt.estimated_price) : null,
      }));
    }

    if (typeof aiData?.summary === "string") {
      summary = aiData.summary.trim();
    }

    if (typeof aiData?.recommendation === "string") {
      recommendation = aiData.recommendation.trim();
    }
  } catch {
    // Keep fallback values
  }
}

const start = new Date(input.start_date);
const end = new Date(input.end_date);
const diffMs = end.getTime() - start.getTime();
const days = Number.isFinite(diffMs) && diffMs > 0 ? Math.ceil(diffMs / 86400000) : 1;
const savingsDaily = Number((target / days).toFixed(2));
const savingsWeekly = Number((savingsDaily * 7).toFixed(2));

return [{
  json: {
    ...input,
    market_price: Number(marketPrice.toFixed(2)),
    alternatives,
    alternatives_sql: JSON.stringify(alternatives).replace(/'/g, "''"),
    analysis_summary: summary || null,
    analysis_recommendation: recommendation || null,
    analysis_summary_sql: summary.replace(/'/g, "''"),
    analysis_recommendation_sql: recommendation.replace(/'/g, "''"),
    savings_daily: savingsDaily,
    savings_weekly: savingsWeekly,
  }
}];

const raw = $json.response ?? $json;
let payload = raw;

if (typeof payload === "string") {
  try {
    payload = JSON.parse(payload);
  } catch {
    payload = { ok: false, error: "Invalid response payload", details: String(raw) };
  }
}

if (payload && typeof payload === "object" && Object.prototype.hasOwnProperty.call(payload, "response")) {
  const nested = payload.response;
  if (typeof nested === "string") {
    try {
      payload = JSON.parse(nested);
    } catch {
      payload = { ok: false, error: "Invalid nested response payload", details: nested };
    }
  } else {
    payload = nested;
  }
}

if (payload?.ok && payload.board && !payload.board.board_code) {
  const fallbackCode = $("Normalize Input").first().json.board_code;
  if (fallbackCode) {
    payload.board = { ...payload.board, board_code: fallbackCode };
  }
}

if (payload?.ok && !Array.isArray(payload.items)) {
  payload.items = [];
}

return [{ json: payload }];

